import React from "react";
import AddEmployeeModal from "../Modal";
import TextField from "@material-ui/core/TextField";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import { setSearchWord, searchEmployees } from "../../redux/actions/actions";
const useStyles = makeStyles({
  search_block: {
    display: "flex",
    alignItems: "center",
    marginBottom: "30px",
  },
});

const SearchBlock = (props) => {
  const { setSearchWord, searchWord } = props;
  const s = useStyles();
  const searchHandler = async (event) => {
    await setSearchWord(event.target.value);
    searchEmployees();
  };

  return (
    <div className={s.search_block}>
      <TextField
        className={s.search_input}
        fullWidth={true}
        onChange={searchHandler}
        id="outlined-basic"
        label="Search"
        variant="outlined"
      />
      <AddEmployeeModal className={s.add_employee} />
    </div>
  );
};
const mapDispatchToProps = {
  setSearchWord,
  searchEmployees,
};
const mapStateToProps = (state) => {
  return {
    searchWord: state.searchWord,
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(SearchBlock);
