import React, { useState } from "react";
import { Button, TextField, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Link } from "react-router-dom";
import { registerUser } from "../redux/actions/actions";
import { connect } from "react-redux";

const useStyles = makeStyles({
  registerBlock: {
    textAlign: "center",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "85vh",
  },
  formBlock: {
    display: "flex",
    padding: "30px",
    width: "25%",
    flexDirection: "column",
    border: "1px solid black",
    borderRadius: "10px",
    backgroundColor: "#f2ffff",
  },
  inputField: {
    backgroundColor: "#fff",
    marginBottom: "20px",
  },
  loginBtn: {
    marginTop: "20px",
  },
  link: {
    color: "#3583d2",
  },
});

const RegisterPage = (props) => {
  const submitHandler = (event) => {
    event.preventDefault();
    registerUser(user);
  };
  const changeHandler = (event) => {
    switch (event.target.name) {
      case "username":
        setUser((prevValue) => ({
          ...prevValue,
          username: event.target.value,
        }));
        break;
      case "password":
        setUser((prevValue) => ({
          ...prevValue,
          password: event.target.value,
        }));
        break;
      case "phone":
        setUser((prevValue) => ({ ...prevValue, phone: event.target.value }));
        break;
      case "email":
        setUser((prevValue) => ({ ...prevValue, email: event.target.value }));
        break;
      default:
        break;
    }
  };
  const { registerUser } = props;
  const s = useStyles();
  const [user, setUser] = useState({
    username: "",
    password: "",
    phone: "",
    email: "",
  });
  return (
    <div className={s.registerBlock}>
      <form onSubmit={submitHandler} className={s.formBlock}>
        <TextField
          name="username"
          onChange={changeHandler}
          className={s.inputField}
          placeholder="Username"
          variant="outlined"
        />
        <TextField
          name="password"
          onChange={changeHandler}
          className={s.inputField}
          placeholder="Password"
          type="password"
          variant="outlined"
        />
        <TextField
          name="email"
          onChange={changeHandler}
          className={s.inputField}
          placeholder="E-Mail"
          variant="outlined"
        />
        <TextField
          name="phone"
          onChange={changeHandler}
          className={s.inputField}
          placeholder="Phone"
          variant="outlined"
        />
        <Typography>
          Already have an account?{" "}
          <Link className={s.link} to="/auth">
            Log in!
          </Link>
        </Typography>
        <Button className={s.loginBtn} variant="contained" color="primary">
          Register
        </Button>
      </form>
    </div>
  );
};
const mapDispatchToProps = {
  registerUser,
};
export default connect(null, mapDispatchToProps)(RegisterPage);
