import React, { useState } from "react";
import { Button, TextField, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Link } from "react-router-dom";
import { registerUser } from "../redux/actions/actions";
import { connect } from "react-redux";

const useStyles = makeStyles({
  registerBlock: {
    textAlign: "center",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "85vh",
  },
  formBlock: {
    display: "flex",
    padding: "30px",
    width: "450px",
    flexDirection: "column",
    border: "1px solid black",
    borderRadius: "10px",
    backgroundColor: "#f2ffff",
  },
  inputField: {
    backgroundColor: "#fff",
    marginBottom: "20px",
  },
  loginBtn: {
    marginTop: "20px",
  },
  link: {
    color: "#3583d2",
  },
  errorMessage: {
    marginTop: "-20px",
  },
});

const RegisterPage = (props) => {
  const { registerUser, registerEmailExist } = props;
  const [user, setUser] = useState({
    username: "",
    password: "",
    phone: "",
    email: "",
  });
  const [usernameError, setUsernameError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);
  const [emailError, setEmailError] = useState(false);
  const [phoneError, setPhoneError] = useState(false);
  const [validationError, setValidationError] = useState(false);
  const submitHandler = (event) => {
    event.preventDefault();
    if (user.username.length <= 3 || user.username.length >= 50) {
      setUsernameError(() => true);
    } else {
      setUsernameError(() => false);
    }
    if (user.password.length <= 6) {
      setPasswordError(() => true);
    } else {
      setPasswordError(() => false);
    }
    if (
      !user.email.includes("@") &&
      !user.email.includes(".") &&
      user.email.length <= 5
    ) {
      setEmailError(() => true);
    } else {
      setEmailError(() => false);
    }
    if (user.phone.length < 11 || user.username.length > 12) {
      setPhoneError(() => true);
    } else {
      setPhoneError(() => false);
    }
    usernameError || emailError || phoneError || passwordError
      ? setValidationError(() => true)
      : setValidationError(() => false);
    if (!validationError) {
      registerUser(user);
    }
  };
  const changeHandler = (event) => {
    event.persist();
    switch (event.target.name) {
      case "username":
        setUser((prevValue) => ({
          ...prevValue,
          username: event.target.value,
        }));
        break;
      case "password":
        setUser((prevValue) => ({
          ...prevValue,
          password: event.target.value,
        }));
        break;
      case "phone":
        setUser((prevValue) => ({ ...prevValue, phone: event.target.value }));
        break;
      case "email":
        setUser((prevValue) => ({ ...prevValue, email: event.target.value }));
        break;
      default:
        break;
    }
  };
  const s = useStyles();
  return (
    <div className={s.registerBlock}>
      <form component="form" onSubmit={submitHandler} className={s.formBlock}>
        <TextField
          name="username"
          onChange={changeHandler}
          className={s.inputField}
          placeholder="Username"
          variant="outlined"
        />
        {usernameError ? (
          <Typography className={s.errorMessage} color="error">
            Enter a correct Username
          </Typography>
        ) : (
          <></>
        )}
        <TextField
          name="password"
          onChange={changeHandler}
          className={s.inputField}
          placeholder="Password"
          type="password"
          variant="outlined"
        />
        {passwordError ? (
          <Typography className={s.errorMessage} color="error">
            Enter a correct Password
          </Typography>
        ) : (
          <></>
        )}
        <TextField
          name="email"
          onChange={changeHandler}
          className={s.inputField}
          placeholder="E-Mail"
          variant="outlined"
        />
        {emailError ? (
          <Typography className={s.errorMessage} color="error">
            Enter a correct E-Mail
          </Typography>
        ) : (
          <></>
        )}
        {registerEmailExist ? (
          <Typography className={s.errorMessage} color="error">
            E-Mail already exist
          </Typography>
        ) : (
          <></>
        )}
        <TextField
          name="phone"
          onChange={changeHandler}
          className={s.inputField}
          placeholder="Phone"
          variant="outlined"
        />
        {phoneError ? (
          <Typography className={s.errorMessage} color="error">
            Enter a correct Phone
          </Typography>
        ) : (
          <></>
        )}
        <Typography>
          Already have an account?{" "}
          <Link className={s.link} to="/auth">
            Log in!
          </Link>
        </Typography>
        <Button
          type="submit"
          className={s.loginBtn}
          variant="contained"
          color="primary"
        >
          Register
        </Button>
      </form>
    </div>
  );
};
const mapDispatchToProps = {
  registerUser,
};
const mapStateToProps = (state) => {
  return {
    registerEmailExist: state.registerEmailExist,
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(RegisterPage);
