import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { getUser } from "../redux/actions/actions";
import CircularProgress from "@material-ui/core/CircularProgress";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles({
  progress: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    width: "100%",
    height: "100vh",
  },
  userPage: {
    backgroundColor: "#f2ffff",
    margin: "40px",
    padding: "30px",
    height: "auto",
    width: "80%",
    display: "flex",
    justifyContent: "space-around",
    border: "1px solid black",
    borderRadius: "5px",
    boxShadow: "3px 3px 10px black",
  },
  pageBlock: {
    display: "flex",
    justifyContent: "center",
  },
  avatar: {
    border: "1px solid black",
    borderRadius: "20px",
  },
  description: {
    fontSize: "40px",
  },
  descriptionBlock: {
    display: "flex",
    flexDirection: "column",
    alignItems: "flex-start",
  },
});

const UserPage = (props) => {
  const { userId } = props.match.params;
  const s = useStyles();
  const { loadingUser, user } = props;
  console.log(user);
  useEffect(() => {
    props.getUser(userId);
  }, []);
  return (
    <div className={s.pageBlock}>
      {loadingUser ? (
        <div className={s.progress}>
          <CircularProgress />
        </div>
      ) : (
        <div className={s.userPage}>
          <img
            className={s.avatar}
            src={"https://robohash.org/" + user._id}
            alt=""
          />
          <div className={s.descriptionBlock}>
            <div>
              <Typography>Name:</Typography>
              <Typography className={s.description}>{user.name}</Typography>
            </div>
            <div>
              <Typography>Phone:</Typography>
              <Typography className={s.description}>{user.phone}</Typography>
            </div>
            <div>
              <Typography>E-Mail:</Typography>
              <Typography className={s.description}>{user.email}</Typography>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
const mapStateToProps = (state) => ({
  user: state.currentUser,
  loadingUser: state.loading,
});
const mapDispatchToProps = {
  getUser,
};
export default connect(mapStateToProps, mapDispatchToProps)(UserPage);
