import React, { useState, useEffect } from "react";
import { Button, TextField, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Link } from "react-router-dom";
import { registerUser } from "../redux/actions/actions";
import { connect } from "react-redux";
import * as yup from "yup";

const useStyles = makeStyles({
  registerBlock: {
    textAlign: "center",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "85vh",
  },
  formBlock: {
    display: "flex",
    padding: "30px",
    width: "450px",
    flexDirection: "column",
    border: "1px solid black",
    borderRadius: "10px",
    backgroundColor: "#f2ffff",
  },
  inputField: {
    backgroundColor: "#fff",
    marginBottom: "20px",
  },
  loginBtn: {
    marginTop: "20px",
  },
  link: {
    color: "#3583d2",
  },
  errorMessage: {
    marginTop: "-20px",
  },
});

const validationSchema = yup.object().shape({
  username: yup.string().required("First name is required field."),
  email: yup
    .string()
    .email("Please enter email address in format: yourname@example.com")
    .required("Please enter email address in format: yourname@example.com"),
  phone: yup.string().required("Phone is required field."),
  password: yup.string().required("Password is required field."),
});

const RegisterPage = (props) => {
  const { registerUser, registerEmailExist } = props;
  const [user, setUser] = useState({
    username: "",
    password: "",
    phone: "",
    email: "",
  });
  const submitHandler = (event) => {
    event.preventDefault();
    registerUser(user);
  };

  const handleInputChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const s = useStyles();
  return (
    <div className={s.registerBlock}>
      <form component="form" onSubmit={submitHandler} className={s.formBlock}>
        <TextField
          error={Boolean(errors.firstName)}
          helperText={errors.firstName}
          name="username"
          onChange={handleInputChange}
          className={s.inputField}
          placeholder="Username"
          variant="outlined"
        />
        {false ? (
          <Typography className={s.errorMessage} color="error">
            Enter a correct Username
          </Typography>
        ) : (
          <></>
        )}
        <TextField
          name="password"
          onChange={handleInputChange}
          className={s.inputField}
          placeholder="Password"
          type="password"
          variant="outlined"
        />
        {false ? (
          <Typography className={s.errorMessage} color="error">
            Enter a correct Password
          </Typography>
        ) : (
          <></>
        )}
        <TextField
          type="email"
          name="email"
          onChange={handleInputChange}
          className={s.inputField}
          placeholder="E-Mail"
          variant="outlined"
        />
        {false ? (
          <Typography className={s.errorMessage} color="error">
            Enter a correct E-Mail
          </Typography>
        ) : (
          <></>
        )}
        {registerEmailExist ? (
          <Typography className={s.errorMessage} color="error">
            E-Mail already exist
          </Typography>
        ) : (
          <></>
        )}
        <TextField
          name="phone"
          onChange={handleInputChange}
          className={s.inputField}
          placeholder="Phone"
          variant="outlined"
        />
        {false ? (
          <Typography className={s.errorMessage} color="error">
            Enter a correct Phone
          </Typography>
        ) : (
          <></>
        )}
        <Typography>
          Already have an account?{" "}
          <Link className={s.link} to="/auth">
            Log in!
          </Link>
        </Typography>
        <Button
          type="submit"
          className={s.loginBtn}
          variant="contained"
          color="primary"
        >
          Register
        </Button>
      </form>
    </div>
  );
};
const mapDispatchToProps = {
  registerUser,
};
const mapStateToProps = (state) => {
  return {
    registerEmailExist: state.registerEmailExist,
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(RegisterPage);
